INSERT INTO `tourism` (`id`, `place_name`, `description`, `city`, `rating`, `lat`, `lon`, `image`) VALUES
(1, 'Monumen Nasional', 'Monumen Nasional atau yang populer disingkat dengan Monas atau Tugu Monas adalah monumen peringatan setinggi 132 meter (433 kaki) yang didirikan untuk mengenang perlawanan dan perjuangan rakyat Indonesia untuk merebut kemerdekaan dari pemerintahan kolonial Hindia Belanda. Pembangunan monumen ini dimulai pada tanggal 17 Agustus 1961 di bawah perintah presiden Soekarno dan dibuka untuk umum pada tanggal 12 Juli 1975. Tugu ini dimahkotai lidah api yang dilapisi lembaran emas yang melambangkan semangat perjuangan yang menyala-nyala. Monumen Nasional terletak tepat di tengah Lapangan Medan Merdeka, Jakarta Pusat.', 'Jakarta', 4.6, -6.17539, 106.827, 'https://assets.pikiran-rakyat.com/crop/0x416:1077x1079/x/photo/2021/01/26/2869470274.jpg'),
(2, 'Kota Tua', 'Kota tua di Jakarta, yang juga bernama Kota Tua, berpusat di Alun-Alun Fatahillah, yaitu alun-alun yang ramai dengan pertunjukan rutin tarian tradisional. Museum Sejarah Jakarta adalah bangunan era Belanda dengan lukisan dan barang antik, sedangkan Museum Wayang memamerkan boneka kayu khas Jawa. Desa Glodok, atau Chinatown, terkenal dengan makanan kaki lima, seperti pangsit dan mi goreng. Di dekatnya, terdapat sekunar dan kapal penangkap ikan di pelabuhan Sunda Kelapa yang kuno', 'Jakarta', 4.6, -6.13764, 106.817, 'https://cdn.antaranews.com/cache/800x533/2021/09/15/kota-tua.jpeg'),
(3, 'Dunia Fantasi', 'Dunia Fantasi atau disebut juga Dufan adalah tempat hiburan yang terletak di kawasan Taman Impian Jaya Ancol, Jakarta Utara, Indonesia. Dufan diresmikan dan dibuka pada tanggal 29 Agustus 1985.', 'Jakarta', 4.6, -6.12531, 106.834, 'https://awsimages.detik.net.id/api/wm/2017/10/18/85ab30e4-84c5-4965-adbb-9bafe0ede8d3_169.jpg?wid=54&w=650&v=1&t=jpeg'),
(4, 'Taman Mini Indonesia Indah (TMII)', 'Taman Mini Indonesia Indah merupakan suatu kawasan taman wisata bertema budaya Indonesia di Jakarta Timur. Area seluas kurang lebih 150 hektare atau 1,5 kilometer persegi ini terletak pada koordinat 6°18\'6.8LS,106°53\'47.2\"BT\"', 'Jakarta', 4.5, -6.30245, 106.895, 'https://upload.wikimedia.org/wikipedia/commons/thumb/d/d6/TMII_Istana_Anak-anak_Indonesia.JPG/250px-TMII_Istana_Anak-anak_Indonesia.JPG'),
(5, 'Atlantis Water Adventure', 'Atlantis Water Adventure atau dikenal dengan Atlantis Ancol akan menyuguhkan petualangan wisata air tak terlupakan. Tempat Wisata bertemakan permainan air dengan luas 5 hektar ini memberi sensasi petualangan di 8 kolam utama. Yaitu kolam Antila, Plaza Atlas, Poseidon, Aquarius, Octopus, Kiddy Pool, dan Atlantean. Berlokasi di kawasan Ancol Jakarta Baycity, Atlantis bisa menjadi pilihan destinasi yang pas untuk wisata berenang.', 'Jakarta', 4.5, -6.12419, 106.839, 'https://cdn.antaranews.com/cache/800x533/2021/12/03/DSC02078.jpg'),
(6, 'Taman Impian Jaya Ancol', 'Taman Impian Jaya Ancol merupakan sebuah objek wisata di Jakarta Utara.\\n\\n', 'Jakarta', 4.5, -6.11733, 106.858, 'https://inakoran.com/uploads/2020/06/13/1592061905-p1e25103da37c44e73efbf5e3809134a1.jpg'),
(7, 'Kebun Binatang Ragunan', 'Kebun Binatang Ragunan adalah sebuah kebun binatang yang terletak di daerah Ragunan, Pasar Minggu, Jakarta Selatan, Indonesia. Kebun binatang seluas 140 hektare ini didirikan pada tahun 1864. Di dalamnya terdapat berbagai koleksi yang terdiri dari 295 spesies dan 4040 spesimen.\\nRagunan sempat ditutup selama sekitar tiga minggu sejak 19 September 2005 karena hewan-hewan di dalamnya ada yang terinfeksi flu burung, tetapi dibuka kembali pada 11 Oktober 2005.Kebun binatang ini memiliki banyak spesies hewan yang langka antara lain kakatua, orangutan, gorila, anoa dan gajah', 'Jakarta', 4.5, -6.31246, 106.82, 'https://www.tagar.id/Asset/uploads2019/1615882455775-ragunan.jpg'),
(8, 'Ocean Ecopark', 'Ocean Ecopark Salah satu zona rekreasi Ancol yang menawarkan ruang terbuka hijau serta pengalaman dan tidak melupakan sisi pendidikan bagi para pengunjung. Beragam aktivitas dan wahana seru yang bisa dimainkan di tempat rekreasi keluarga, beberapa diantaranya : Outbondholic, Rumah Energy, Rumah Lebah, Kano, Paintball, Outbondholic, Eco-Market, Learning Farm, Eco-Bike, Faunaland. Kalau berencana berwisata ke Ocean Eco Park ancol tidak perlu khawatir dengan harga tiket masuknya. Karena harga tiket masuk Ocean Eco Park ditawarkan dengan harga yang masih terjangkau.', 'Jakarta', 4, -6.1258, 106.836, 'https://media-cdn.tripadvisor.com/media/photo-s/0f/14/cc/bc/ecopark.jpg'),
(9, 'Pelabuhan Marina', 'Pelabuhan Marina Ancol berada di kawasan Taman Impian Jaya Ancol, Jakarta. Pelabuhan yang biasa disebut Dermaga Marina Ancol ini adalah kawasan yang tidak hanya untuk keperluan penyeberangan saja, tetapi juga dikembangkan untuk banyak hal, seperti wisata air, olahraga air maupun wahana bermain. Fungsi utama dari pelabuhan ini adalah untuk melayani para penumpang yang ingin menyeberang menuju ke kepulauan Seribu, begitu juga sebaliknya. Banyak orang yang berada di Jakarta dan sekitarnya, menjadikan kepulaun Seribu sebagai tujuan wisata yang utama. Liburan di Pulau Seribu memang mampu menghilangkan kepenatan bekerja di kota metropolitan, sembari menikmati keindahan pantai yang masih terjaga dengan baik. Marina Ancol berada di Jakarta Utara atau secara lengkap beralamat di Kota Tua, Ancol, Pademangan, Kota Jakarta. Sangat mudah menuju ke tempat ini karena tidak terlalu jauh dari pusat kota. Selain itu, ada banyak transportasi umum yang menuju ke tempat ini atau bisa juga menggunakan jasa transportasi online.', 'Jakarta', 4.4, 1.07888, 103.931, 'https://www.hotelmurah.com/ceritawisata/wp-content/uploads/2019/09/pelabuhan-marina.jpg'),
(10, 'Pulau Tidung', 'Pulau Tidung adalah salah satu kelurahan di kecamatan Kepulauan Seribu Selatan, Kabupaten Kepulauan Seribu, Jakarta, Indonesia.', 'Jakarta', 4.5, -5.80321, 106.524, 'https://backpackerjakarta.com/wp-content/uploads/2016/09/pulau_tidung_besar_by_rozeki-d4b2ig5.jpg'),
(11, 'Pulau Bidadari', 'Pulau Bidadari merupakan salah satu resor di Kabupaten Kepulauan Seribu, Jakarta. Sebelum bernama Pulau Bidadari, pulau ini memiliki dua nama yaitu Pulau Sakit dan Pulau Purmerend', 'Jakarta', 4.6, -6.03583, 106.747, 'https://www.ancol.com/blog/wp-content/uploads/2023/02/pulau-bidadari.jpeg'),
(12, 'Danau Shuji Lembak', '\"Danau Shuji Lembak adalah sebuah danau yang terletak di suatu lokasi tertentu. Tempat ini menawarkan pemandangan indah dan suasana yang tenang. Pengunjung dapat menikmati kegiatan seperti berjalan-jalan di sekitar danau, menikmati pemandangan alam, atau berpartisipasi dalam aktivitas air seperti berperahu atau memancing.\"', 'Muara Enim', 4.5, -3.35462, 104.347, 'https://asset.kompas.com/crops/VBs3m61LLR1nUKWrHhcq9k1BUx8=/0x0:0x0/750x500/data/photo/2021/02/22/6033ac69a2cb6.jpg'),
(13, 'Desa Wisata Kampung Warna Burai', '\"Desa Wisata Kampung Warna Burai adalah sebuah desa yang menawarkan pengalaman unik dengan dekorasi warna-warni yang mencolok. Di sini, pengunjung dapat menikmati berbagai bangunan dan jalan yang dihiasi dengan warna-warna cerah yang menciptakan suasana yang ceria. Desa ini juga mungkin menawarkan toko-toko kecil, kafe, atau galeri seni yang menampilkan produk lokal.\"', 'Ogan Ilir ', 4.4, -3.28065, 104.595, 'https://img.celebrities.id/okz/900/87ljB1/master_0vo165jEK9_1570_desa_burai.jpg'),
(14, 'Kampoeng Malaka Kartamulia', '\"Kampoeng Malaka Kartamulia adalah suatu kampung yang menampilkan kebudayaan dan tradisi lokal. Pengunjung dapat melihat dan mengalami kehidupan sehari-hari penduduk setempat, menjelajahi arsitektur tradisional, serta mencoba makanan dan kerajinan tangan khas daerah tersebut. Kampung ini juga mungkin menawarkan aktivitas budaya dan acara yang melibatkan komunitas lokal.\"', 'Muara Enim ', 4.3, -3.21191, 104.36, 'https://i.ytimg.com/vi/pGPAyX8GCp4/maxresdefault.jpg'),
(15, 'Wisata Alam Pondok Marogan', '\"Wisata Alam Pondok Marogan adalah destinasi wisata alam yang menawarkan pemandangan yang indah dan suasana yang tenang. Anda dapat menikmati keindahan alam, seperti hutan, sungai, dan pemandangan alam lainnya di sekitar Pondok Marogan.\"', 'Palembang', 4.8, -2.88132, 104.701, 'https://assets.ayobandung.com/crop/0x0:0x0/750x500/webp/photo/detiksumsel/2020/07/IMG-20200711-WA0016.jpg'),
(16, 'Taman Permata', '\"Taman Permata adalah taman rekreasi yang menyediakan ruang terbuka hijau dan fasilitas rekreasi. Di taman ini, Anda dapat bersantai, berjalan-jalan, atau melakukan piknik bersama keluarga dan teman-teman. Mungkin terdapat area bermain untuk anak-anak dan fasilitas olahraga.\"', 'Musi Banyuasin ', 4, -2.87931, 103.837, 'https://www.andalastourism.com/wp-content/uploads/2020/04/Taman-Permata-Musi.jpg'),
(17, 'Kampung Hias Lorong Mari', '\"Kampung Hias Lorong Mari adalah sebuah kampung yang terkenal dengan dekorasi dan ornamen yang indah di setiap lorongnya. Anda dapat menjelajahi kampung ini, melihat-lihat dekorasi yang unik, dan menikmati suasana kampung yang khas.\"', 'Palembang', 4.8, -3.00162, 104.817, 'https://a.cdn-myedisi.com/n_article/palpos-5814-5e456a6ebfb1b.jpg'),
(18, 'Taman Kota Sekayu', '\"Taman Kota Sekayu adalah taman kota yang menyediakan ruang terbuka hijau dan tempat rekreasi di pusat kota. Di taman ini, Anda dapat berjalan-jalan, bersantai, atau berolahraga. Mungkin terdapat area bermain untuk anak-anak, taman bunga, dan fasilitas rekreasi lainnya.\"', 'Musi Banyuasin', 4.7, -2.87538, 103.838, 'https://fornews.co/news/inline/2021/11/taman-Sekayu.jpg'),
(19, 'Taman Sekanak Lambidaro', '\"Taman Sekanak Lambidaro adalah taman rekreasi yang menawarkan suasana alam yang asri. Anda dapat menikmati pemandangan hijau, air terjun, dan fasilitas rekreasi lainnya di taman ini.\"', 'Palembang', 4.5, -2.98518, 104.752, 'https://blue.kumparan.com/image/upload/fl_progressive,fl_lossy,c_fill,q_auto:best,w_640/v1643792318/mgm1l6pj3uzb5pcsolm3.jpg'),
(20, 'Taman Danau', '\"Taman Danau adalah taman yang terletak di sekitar danau. Anda dapat menikmati pemandangan danau yang indah, berjalan-jalan di sekitar taman, atau duduk bersantai sambil menikmati keindahan alam.\"', 'Palembang', 4.6, -3.01386, 104.795, 'https://cdn-2.tstatic.net/sumsel/foto/bank/images/pengunjung-dana-jsc-palembang.jpg'),
(21, 'Playground Simpang Tiga Stairs Takat', '\"Playground Simpang Tiga Stairs Takat adalah tempat bermain anak-anak yang terletak di Simpang Tiga Stairs Takat. Tempat ini menyediakan area bermain dengan perosotan, ayunan, dan fasilitas lainnya untuk anak-anak.\"', 'Palembang', 4.3, -2.98969, 104.792, 'https://live.staticflickr.com/826/40966956985_4453086c62_b.jpg'),
(22, 'JB PARK', '\"JB PARK adalah sebuah taman rekreasi yang menawarkan berbagai fasilitas dan hiburan. Di taman ini, pengunjung dapat menikmati wahana permainan, area bermain, lapangan olahraga, atau hanya bersantai di area hijau. JB PARK juga mungkin menyediakan tempat makan, toko suvenir, atau area piknik untuk keluarga dan teman-teman berkumpul.\"', 'Muara Enim', 5, -3.22947, 104.347, 'https://i3.wp.com/vasadnews.com/wp-content/uploads/2021/01/20210102_183142-300x169.jpg'),
(23, 'Taman Kota Pangkalan Balai', '\"Taman Kota Pangkalan Balai adalah taman rekreasi yang terletak di kota Pangkalan Balai. Di taman ini, Anda dapat menikmati area bermain, fasilitas olahraga, dan ruang terbuka hijau untuk bersantai.\"', 'Banyuasin', 4.1, -2.90506, 104.41, 'https://www.swarnanews.co.id/wp-content/uploads/2020/08/IMG-20200802-WA0005.jpg'),
(24, 'Objek Wisata Danau Batanghari Siku', '\"Objek Wisata Danau Batanghari Siku adalah tempat wisata yang terletak di sekitar danau Batanghari Siku. Anda dapat menikmati pemandangan danau, melakukan aktivitas air, atau bersantai di sekitar danau.\"', 'Penukal Abab Lematang Ilir ', 4.3, -3.33256, 103.995, 'https://www.andalastourism.com/wp-content/uploads/2020/04/Danau-Batanghari-Siku.jpg.webp'),
(25, 'SIMPANG EMPAT TUGU HUTAN LARANGAN', '\"SIMPANG EMPAT TUGU HUTAN LARANGAN adalah lokasi yang memiliki tugu atau monumen yang menjadi landmark atau titik pertemuan empat jalan di Hutan Larangan. Tempat ini memiliki nilai sejarah dan dapat menjadi landmark yang menarik untuk dikunjungi.\"', 'Banyuasin ', 4.1, -2.89627, 104.385, 'https://upload.wikimedia.org/wikipedia/commons/thumb/4/48/Bundaran_Tugu_Hutan_Larangan_-_Pangkalan_Balai%2C_Banyuasin_III%2C_Banyuasin%2C_SS_%2829_January_2021%29.jpg/1024px-Bundaran_Tugu_Hutan_Larangan_-_Pangkalan_Balai%2C_Banyuasin_III%2C_Banyuasin%2C_SS_%2829_January_2021%29.jpg'),
(26, 'Danau Palem Raya', '\"Danau Palem Raya adalah danau yang terletak di daerah Palembang. Danau ini menawarkan pemandangan yang indah dan suasana yang tenang. Anda dapat menikmati keindahan alam, berjalan-jalan di sekitar danau, atau melakukan aktivitas air seperti berperahu atau memancing.\"', 'Ogan Ilir ', 4.4, -3.2166, 104.698, 'https://pbs.twimg.com/media/ExiGP_ZU4AIwuCz.jpg'),
(27, 'Kampoeng Bingen Palembang', '\"Kampoeng Bingen Palembang adalah kampung yang memiliki keunikan dan daya tarik tersendiri. Anda dapat menjelajahi kampung ini, melihat-lihat arsitektur tradisional, berinteraksi dengan penduduk setempat, dan mencoba makanan atau kerajinan khas daerah tersebut.\"', 'Palembang ', 4.5, -2.98567, 104.774, 'https://pict.sindonews.net/dyn/620/pena/news/2020/07/21/720/108772/kampung-bingen-palembang-simpan-sejarah-kota-tua-htm.png'),
(28, 'Kolam Renang Lombok Kenten Laut', '\"Kolam Renang Lombok Kenten Laut adalah tempat rekreasi yang menyediakan kolam renang untuk umum. Anda dapat bersantai, berenang, atau bermain air di kolam renang ini. Tempat ini juga mungkin dilengkapi dengan fasilitas lain seperti kafe atau area bermain anak.\"', 'Banyuasin ', 4.2, -2.8844, 104.776, 'https://t-2.tstatic.net/sumsel/foto/bank/images/Lokasi-Kolam-Renang-Lombok-Kenten-di-Tanah-MAS-Kecamatan-Talang-Kelapa-Banyuasin-Sumsel.jpg'),
(29, 'Taman Pelangi Sriwijaya', '\"Taman Pelangi Sriwijaya adalah taman yang memiliki tema warna-warni pelangi. Anda dapat menikmati suasana yang cerah dan riang di taman ini. Taman ini mungkin memiliki berbagai dekorasi pelangi, bunga-bunga warna-warni, dan fasilitas rekreasi untuk dinikmati oleh pengunjung.\"', 'Palembang ', 3.6, -3.02768, 104.786, 'https://assets.ayobandung.com/crop/0x0:0x0/750x500/webp/photo/2022/08/19/1937518778.jpg'),
(30, 'Taman Selfie Putri Wisata', '\"Taman Selfie Putri Wisata adalah taman yang dirancang khusus untuk foto selfie. Tempat ini memiliki berbagai spot foto menarik, dekorasi unik, dan latar belakang yang indah. Anda dapat mengabadikan momen dan mengambil foto kreatif di taman ini.\"', 'Banyuasin ', 4, -2.80636, 104.216, 'https://1.bp.blogspot.com/-Dx3s2zzF9ts/YBRZV1h-dwI/AAAAAAAAGFg/qKsOFqjD3RobuVFziDAROAcYPQW-sOfbQCLcBGAsYHQ/s452/taman-selfie-wisata-betung.jpg'),
(31, 'Kuto Besak Fortress', '\"Kuto Besak Fortress adalah benteng sejarah yang terletak di Palembang. Benteng ini memiliki nilai sejarah dan arsitektur yang menarik. Anda dapat menjelajahi benteng ini, mempelajari sejarahnya, dan menikmati pemandangan sekitar.\"', 'Palembang ', 4.5, -2.99137, 104.759, 'https://traverse.id/wp-content/uploads/2018/01/Benteng-Kuto-Besak-Palembang-@himsaifanah-1024x683.jpg'),
(32, 'Tugu Ikan Belido', '\"Tugu Ikan Belido adalah tugu atau monumen yang menggambarkan ikan belido, ikan khas daerah Palembang. Tugu ini merupakan landmark atau titik orientasi yang dapat menjadi tempat menarik untuk dikunjungi dan berfoto.\"', 'Palembang ', 4.5, -2.99274, 104.76, 'https://s.kaskus.id/r540x540/images/2018/01/26/9250512_201801260725100556.jpg'),
(33, 'Kampoeng Selarai Indah', '\"Kampoeng Selarai Indah adalah kampung yang memiliki keindahan alam dan nuansa tradisional. Anda dapat menikmati suasana kampung yang tenang, melihat-lihat arsitektur tradisional, dan berinteraksi dengan penduduk setempat.\"', 'Musi Banyuasin ', 4.3, -2.84506, 103.834, 'https://statik.tempo.co/data/2022/11/25/id_1160050/1160050_720.jpg'),
(34, 'Culinary Musi Riverside', '\"Culinary Musi Riverside adalah tempat yang menawarkan pengalaman kuliner yang unik di tepi Sungai Musi. Anda dapat menemukan berbagai jenis makanan dan minuman khas Palembang serta menikmati pemandangan sungai yang indah.\"', 'Palembang ', 4.3, -2.9894, 104.763, 'https://3.bp.blogspot.com/-4D9rUI6-2QE/U15yosHfXII/AAAAAAAAScU/4GSgwIc8Jh4/s1600/Riverside-Restaurant-Palembang-Sumatera-Selatan-Indonesia.jpg'),
(35, 'Danau Segayam', '\"Danau Segayam adalah danau yang terletak di daerah tertentu. Danau ini menawarkan pemandangan alam yang indah dan suasana yang tenang. Anda dapat menikmati keindahan alam, berjalan-jalan di sekitar danau, atau melakukan aktivitas air seperti berperahu atau \"', 'Muara Enim ', 4.6, -3.25551, 104.545, 'https://indosiber.id/wp-content/uploads/2021/01/IMG-20210103-WA0098.jpg'),
(36, 'Amanzi Waterpark', '\"Amanzi Waterpark adalah taman air yang menawarkan berbagai wahana dan fasilitas air seru. Anda dapat menikmati kolam renang, seluncuran air, area bermain air, dan berbagai aktivitas seru lainnya di taman air ini.\"', 'Palembang ', 4.4, -2.93794, 104.684, 'https://s-light.tiket.photos/t/01E25EBZS3W0FY9GTG6C42E1SE/rsfit19201280gsm/events/2020/11/22/df634b2e-5bd2-4cf6-9e56-3434f43f0989-1606014244570-cf6e276c9bfe10c151c9dc54ddb06004.jpg'),
(37, 'Taman Bunga Celosia Kenten Laut', '\"Taman Bunga Celosia Kenten Laut adalah taman yang terkenal dengan keindahan bunga Celosia yang tumbuh di sana. Anda dapat menikmati pemandangan bunga yang indah, berjalan-jalan di antara kebun bunga, dan mengambil foto-foto yang cantik.\"', 'Banyuasin ', 4.3, -2.88338, 104.776, 'https://assets.ayobandung.com/crop/0x0:0x0/750x500/webp/photo/2023/04/21/42978356-6eb9-4925-9907-a01eec58fcd1-736375827.jpeg'),
(38, 'Wisata Air Bebek Aye', '\"Wisata Air Bebek Aye adalah tempat wisata yang terkenal dengan sajian bebek yang lezat. Anda dapat menikmati hidangan bebek yang khas, seperti bebek goreng atau bebek bakar, sambil menikmati suasana yang nyaman dan pemandangan sekitar.\"', 'Banyuasin ', 4.1, -3.04803, 104.79, 'https://4.bp.blogspot.com/-1zd0eblEAqI/UYi7kmmEm_I/AAAAAAAAAu0/NsV2MBTec8o/s1600/Dermaga.jpg'),
(39, 'Pondok Bambu', '\"Pondok Bambu adalah tempat yang menawarkan penginapan atau pengalaman menginap dengan konsep pondok yang terbuat dari bambu. Anda dapat merasakan suasana alami, nyaman, dan tradisional di pondok yang terbuat dari bambu.\"', 'Palembang ', 4.3, -3.02503, 104.652, 'https://cdn-2.tstatic.net/sumsel/foto/bank/images2/Viral-tempat-wisata-pondok-bambu-di-Palembang-Pulokerto-Gandus-dengan-harga-tiket-masuk-murah.jpg'),
(40, 'Taman asih Sidomulyo', '\"Taman Asih Sidomulyo adalah taman rekreasi yang menyediakan ruang terbuka hijau dan fasilitas rekreasi. Di taman ini, Anda dapat bersantai, berjalan-jalan, atau melakukan piknik bersama keluarga dan teman-teman. Mungkin terdapat area bermain untuk anak-anak dan fasilitas olahraga.\"', 'Banyuasin ', 4.4, -2.94891, 105.007, 'https://i.ytimg.com/vi/a0VFT97T4o4/maxresdefault.jpg'),
(41, 'Tanjung Senai', '\"Tanjung Senai adalah sebuah tempat yang terletak di daerah tertentu. Tempat ini mungkin memiliki pemandangan pantai atau tepi laut yang indah. Anda dapat menikmati pemandangan laut, berjalan-jalan di sekitar pantai, atau menikmati kegiatan air seperti berenang atau berperahu.\"\r\n', 'Ogan Ilir \r\n', 4.5, -3.25529, 104.65, 'https://lh3.googleusercontent.com/-rkyTD9SMkzI/YqWjrgHJ-sI/AAAAAAAAOkY/RARYaZSvJckenZXi_xAbZ2VlK3w9hdBgQCNcBGAsYHQ/s1600/1655022507974952-0.png'),
(42, 'Taman Wisata Kerajaan Sriwijaya\r\n', '\"Taman Purbakala Kerajaan Sriwijaya atau sebelumnya dikenal dengan nama Situs Karanganyar adalah taman purbakala bekas kawasan permukiman dan taman yang dikaitkan dengan kerajaan Sriwijaya yang terletak tepi utara Sungai Musi di kota Palembang, Sumatra Selatan.\"', 'Palembang \r\n', 4.4, -3.01503, 104.734, 'https://upload.wikimedia.org/wikipedia/commons/thumb/0/02/Taman_Purbakala_Kerajaan_Sriwijaya_-_Pendopo_Utama.jpg/1280px-Taman_Purbakala_Kerajaan_Sriwijaya_-_Pendopo_Utama.jpg'),
(43, 'Taman Kolam Bahari\r\n', '\"Taman Kolam Bahari Palembang adalah taman yang terletak di jantung kota Palembang. Taman ini memiliki kolam buatan yang luas dan di sekitarnya terdapat bangku-bangku yang dapat digunakan untuk duduk santai. Selain itu, terdapat juga berbagai macam tanaman hias yang membuat taman ini semakin indah.\"\r\n', 'Palembang \r\n', 4.8, -2.95739, 104.771, 'https://cdn0-production-images-kly.akamaized.net/q5alSvPS2VeRR2HdihkYRk7R89Q=/1200x675/smart/filters:quality(75):strip_icc():format(jpeg)/kly-media-production/medias/3487994/original/024375900_1624202285-WhatsApp_Image_2021-06-20_at_10.08.24_PM__2_.jpeg'),
(44, 'Lembah Sakura Palembang\r\n', '\"Lembah Sakura Palembang adalah taman wisata yang terletak di daerah Palembang. Taman ini memiliki keindahan yang menarik dengan banyaknya pohon sakura dan bunga-bunga yang tumbuh di sekitarnya. Selain itu, taman ini juga dilengkapi dengan berbagai fasilitas seperti kolam ikan, area bermain anak, dan beberapa wahana permainan. Taman Lembah Sakura Palembang menjadi salah satu tempat yang cukup populer di kota Palembang dan sering dikunjungi oleh wisatawan yang ingin menikmati keindahan alam sekitar.\"\r\n', 'Palembang \r\n', 4.3, -3.00449, 104.706, 'https://cdn-2.tstatic.net/lampung/foto/bank/images/bukit-sakura_20180516_162241.jpg'),
(45, 'Taman Kambang Iwak Besak', '\"Taman Kambang Iwak atau biasa juga disebut Ki Park merupakan taman kota yang berada di Palembang, dan menjadi tempat favorit warga sekitar untuk menghabiskan waktu bersama keluarga, teman, atau komunitas lainnya.Taman ini bisa dibilang termasuk taman yang legendaris, karena sudah ada sejak tahun 1900-an yakni sejak Kolonial Belanda masih menguasai Indonesia.\"', 'Palembang \r\n', 4.5, -2.9902, 104.747, 'https://media-cdn.tripadvisor.com/media/photo-s/07/69/7b/d3/gerbang-masuk.jpg'),
(46, 'Pusri Deer Park\r\n', '\"Taman Rusa PUSRI Palembang merupakan taman dengan hewan rusa di kawasan komplek PT Pusri Palembang. Tepatnya di seberang RS Pusri. \"\r\n', 'Palembang \r\n', 4.3, -2.9691, 104.802, 'https://farm5.staticflickr.com/4166/34574789005_1dea14c544_z.jpg'),
(47, 'Danau Teluk Seruo\r\n', '\"Danau Teluk Seruo adalah danau alami yang terletak di daerah tertentu. Danau ini menawarkan pemandangan alam yang indah dan suasana yang tenang. Anda dapat menikmati keindahan danau, berjalan-jalan di sekitarnya, atau melakukan aktivitas air seperti memancing.\"\r\n', 'Ogan Ilir \r\n', 4.3, -3.25878, 104.644, 'https://direktoripariwisata.id/imgunit/3381.jpg'),
(48, 'Saung Bambu Pelangi Kenten\r\n', '\"Saung Bambu Pelangi Kenten adalah tempat yang menawarkan suasana tradisional dengan saung atau pondok yang terbuat dari bambu. Anda dapat menikmati suasana alami, nyaman, dan tradisional di saung-saung yang terbuat dari bambu.\"\r\n', 'Banyuasin \r\n', 4.4, -2.88651, 104.774, 'https://sumeks.disway.id/upload/57effa60ae70360b4b128dedc2433b4f.jpeg'),
(49, 'Waterpark Fantasy Island\r\n', '\"Fantasy Island adalah salah satu taman bermain air atau waterpark yang ada di Palembang. Tempat wisata ini selalu ramai oleh masyarakat yang ingin menghabiskan hari libur dengan berenang maupun sekadar bermain air.\"\r\n', 'Palembang \r\n', 4.3, -3.07692, 104.726, 'https://1.bp.blogspot.com/-BICBr3H4SsA/YY8lbf4aEjI/AAAAAAAAOOk/PUm8F2rJxcAGr6ZVYXTET6n45HsQXArdACLcBGAsYHQ/s720/fantasy-island-palembang.jpg'),
(50, 'Bayt Al-Qur\'an Palembang\r\n', '\"Bayt Al Quran Al-Akbar (Museum Al Quran Raksasa) adalah museum yang berada di kota Palembang[1] beralamat di Pondok Pesantren Al Ihsaniyah Gandus Palembang. Terdapat 30 juz ayat suci Al-Quran yang berhasil dipahat/diukir ala khas Palembang dalam lembar kayu dan menghabiskan kurang lebih 40 meter kubik kayu tembesu dengan biaya tidak kurang Rp 2 miliar, dimana masing-masing lembar ukuran halamannya 177 x 140 x 2,5 sentimeter dan tebal keseluruhannya termasuk sampul mencapai 9 meter.\"\r\n', 'Palembang \r\n', 4.6, -3.01109, 104.704, 'https://tafsiralquran.id/wp-content/uploads/2020/10/IMG_9063-Copy.jpg'),
(51, 'Palembang Bird Park', '\"Taman wisata Palembang Bird Park terletak di kawasan OPI Mall Jakabaring Palembang, tidak jauh dari OPI Water Fun Jakabaring. Di sini menjadi taman burung karena menyediakan lebih dari 100 jenis burung, \" burung parkit, jalak, kakak tua, kenari, zebra, dan jenis lainnya. Wisata ini cocok sebagai pilihan keluarga, terutama untuk anak-anak.\r\n', 'Palembang \r\n', 4.3, -3.03646, 104.788, 'https://pergiyuk.com/wp-content/uploads/2022/09/Palembang-Bird-Park.jpg'),
(52, 'Wisata Tirto Mulyo Desa Tegal Mulyo\r\n\r\n', '\"Wisata Tirto Mulyo Desa Tegal Mulyo adalah tempat wisata yang terletak di desa Tegal Mulyo. Tempat ini mungkin menawarkan berbagai atraksi dan kegiatan, seperti taman rekreasi, air terjun, atau tempat bermain untuk anak-anak.\"\r\n', 'Musi Banyuasin \r\n\r\n', 4.5, -2.62187, 104.004, 'https://jadesta.kemenparekraf.go.id/imgpost/69720.jpg'),
(53, 'Waterfront Sekayu\r\n', '\"Waterfront Sekayu adalah sebuah area tepi sungai atau danau di Sekayu. Tempat ini menawarkan pemandangan indah dan suasana yang nyaman. Anda dapat menikmati pemandangan air, berjalan-jalan di sepanjang waterfront, atau bersantai di area rekreasi yang disediakan.\"\r\n', 'Musi Banyuasin \r\n', 4.3, -2.87586, 103.832, 'https://images.bisnis.com/posts/2020/07/13/1265083/sky-water-front_muba1.jpg'),
(54, 'Sekanak Sidewalk\r\n', '\"Sekanak Sidewalk adalah sebuah jalan trotoar yang terletak di Sekanak. Tempat ini mungkin menawarkan area pejalan kaki yang luas dengan berbagai fasilitas, seperti taman, area bermain, atau tempat duduk yang nyaman.\"\r\n', 'Palembang \r\n', 4.3, -2.99151, 104.756, 'https://nowpalembang.com/wp-content/uploads/2022/10/sekanak-01.jpeg'),
(55, 'Taman jokis\r\n', '\"Taman Jokis adalah taman rekreasi yang menawarkan ruang terbuka hijau dan fasilitas rekreasi. Di taman ini, Anda dapat bersantai, berjalan-jalan, atau melakukan piknik bersama keluarga dan teman-teman. Mungkin terdapat area bermain untuk anak-anak dan fasilitas olahraga.\"\r\n', 'Banyuasin \r\n', 4.4, -2.88396, 104.776, 'https://atourin.obs.ap-southeast-3.myhuaweicloud.com/images/destination/palembang/taman-bunga-celosia-spring-hill-palembang-profile1646287254.png'),
(56, 'Danau Buatan Air Batu\r\n', '\"Danau Buatan Air Batu adalah danau buatan yang terletak di daerah tertentu. Danau ini mungkin memiliki pemandangan yang indah dan juga fasilitas, seperti area bermain air atau wahana air.\"\r\n', 'Banyuasin \r\n', 4.3, -2.90307, 104.618, 'https://infopalembang.id/wp-content/uploads/2015/11/4-1024x768.jpg'),
(57, 'Kampoeng Wisata Yasaman Cindo\r\n', '\"Kampoeng Wisata Yasaman Cindo adalah tempat wisata yang menawarkan pengalaman budaya dan tradisi. Anda dapat mengenal lebih dekat kebudayaan dan tradisi daerah tersebut, melihat pertunjukan seni tradisional, dan menikmati kuliner khas.\"\r\n', 'Banyuasin \r\n', 4.3, -3.11267, 104.911, 'https://img.inews.co.id/media/822/files/inews_new/2023/02/21/kampung_wisata_yasaman_cindo.jpg'),
(58, 'Kampung Wisata Al Munawar\r\n', '\"Kampung Al-Munawar adalah kampung yang dihuni oleh warga keturunan Arab. Menilik usia rumah-rumah yang berada disana, masyarakat Arab mulai mendiami kampung ini sejak 350 juta tahun yang lalu. Hal ini tentunya tidak mengejutkan karena Palembang memang merupakan salah satu kota tertua di Indonesia.\"\r\n', 'Palembang \r\n', 4.6, -2.98782, 104.773, 'https://cdn0-production-images-kly.akamaized.net/mEf3s2f1-OEtHKBcyNbA5yvMVpg=/1200x665/smart/filters:quality(75):strip_icc():format(webp)/kly-media-production/medias/3435944/original/062183400_1619019398-WhatsApp_Image_2021-04-21_at_10.32.32_PM.jpeg'),
(59, 'Taman Wisata Alam Punti Kayu\r\n', '\"Taman Wisata Alam (TWA) Punti Kayu adalah sebuah kawasan pelestarian alam yang dimanfaatkan untuk kegiatan pariwisata alam dan rekreasi di Palembang, Sumatra Selatan . Terletak di tengah kota Palembang - tepatnya di kawasan Km.7 Palembang, Punti Kayu menjadi tempat liburan favorit yang ramai dikunjungi warga kota Palembang khususnya pada akhir pekan dan hari-hari libur.\"\r\n', 'Palembang \r\n', 4.3, -2.94521, 104.726, 'https://static.republika.co.id/uploads/images/inpicture_slide/rencana-pengembangan-fasilitas-semi-wild-pengelolaan-satwa-di_210528145049-208.jpg'),
(60, 'Masjid Istiqlal\r\n', 'Masjid Istiqlal (arti harfiah: Masjid Merdeka) adalah masjid nasional negara Republik Indonesia yang terletak di bekas Taman Wilhelmina, di Timur Laut Lapangan Medan Merdeka yang di tengahnya berdiri Monumen Nasional (Monas), di pusat ibukota Jakarta. Masjid ini merupakan salah satu dari 10 masjid terbesar kapasitasnya di dunia yang dapat menampung lebih dari 200.000 jamaah. Di seberang Timur masjid ini berdiri Gereja Katedral Jakarta. Ketua Harian Badan Pengelola Masjid Istiqlal (BPMI) sekaligus Imam Besar Masjid Istiqlal adalah Prof. Dr. Nasaruddin Umar, M.A. Pembangunan masjid ini diprakarsai oleh Presiden Republik Indonesia saat itu, Ir. Soekarno. Peletakan batu pertama sebagai tanda dimulainya pembangunan Masjid Istiqlal dilakukan oleh Ir. Soekarno pada tanggal 24 Agustus 1961. Arsitek Masjid Istiqlal adalah Frederich Silaban, seorang Nasrani.Masjid ini memiliki gaya arsitektur modern dengan dinding dan lantai berlapis marmer, dihiasi ornamen geometrik dari baja antikarat. Bangunan utama masjid ini terdiri dari lima lantai dan satu lantai dasar. Bangunan utama itu dimahkotai satu kubah besar berdiameter 45 meter yang ditopang 12 tiang besar. Menara tunggal setinggi total 96,66 meter menjulang di sudut Selatan selasar masjid. Masjid ini mampu menampung lebih dari 200.000 jamaah.Selain digunakan sebagai aktivitas ibadah umat Islam, masjid ini juga digunakan sebagai kantor berbagai organisasi Islam di Indonesia, aktivitas sosial, dan kegiatan umum. Masjid ini juga menjadi salah satu daya tarik wisata yang terkenal di Jakarta. Kebanyakan wisatawan yang berkunjung umumnya wisatawan domestik di samping sebagian wisatawan asing yang beragama Islam. Masyarakat non-Muslim juga dapat berkunjung ke masjid ini setelah sebelumnya mendapat pembekalan informasi mengenai Islam dan Masjid Istiqlal, meskipun bagian yang boleh dikunjungi kaum non-Muslim terbatas dan harus didampingi pemandu.\\nPada tiap hari besar Islam seperti Ramadhan, Idul Fitri, Idul Adha, Tahun Baru Hijriyah, Maulid Nabi Muhammad serta Isra dan Mi\'raj, Presiden Republik Indonesia selalu mengadakan kegiatan keagamaan di masjid ini yang disiarkan secara langsung melalui televisi nasional (TVRI) dan sebagian televisi swasta.\r\n', 'Jakarta\r\n', 4.7, -6.17017, 106.831, 'https://indonesiakaya.com/wp-content/uploads/2020/10/masjid_istiqlal_1200.jpg'),
(61, 'Museum Nasional\r\n', 'Museum Nasional Republik Indonesia atau Museum Gajah, adalah sebuah museum arkeologi, sejarah, etnografi, dan geografi yang terletak di Jakarta Pusat dan persisnya di Jalan Merdeka Barat 12. Museum ini merupakan museum pertama dan terbesar di Asia Tenggara.\r\n', 'Jakarta\r\n', 4.6, -6.1764, 106.822, 'https://upload.wikimedia.org/wikipedia/commons/thumb/4/45/Museum_Nasional_Indonesia.jpg/640px-Museum_Nasional_Indonesia.jpg'),
(62, 'Pasar Tanah Abang\r\n', 'Pasar Tanah Abang atau Pasar Sabtu dibangun oleh Yustinus Vinck pada 30 Agustus 1735. Yustinus Vinck mendirikan Pasar Tanah Abang Pasar atas izin dari Gubernur Jenderal Abraham Patramini. Izin yang diberikan saat itu untuk Pasar Tanah Abang adalah untuk berjualan tekstil serta barang kelontong dan hanya buka setiap hari Sabtu. Oleh karena itu, pasar ini disebut Pasar Sabtu. Pasar ini mampu menyaingi Pasar Senen (Welter Vreden) yang sudah lebih dulu maju.\\nPada tahun 1740 terjadi Peristiwa Geger Pecinan, yaitu pembantaian orang-orang Tionghoa, perusakan harta benda, termasuk Pasar Tanah Abang diporakporandakan dan dibakar. Pada tahun 1881, Pasar Tanah Abang kembali dibangun dan yang tadinya dibuka pada hari Sabtu, ditambah hari Rabu, sehingga Pasar Tanah Abang dibuka 2 kali seminggu. Bangunan Pasar pada mulanya sangat sederhana,terdiri dari dinding bambu dan papan serta atap rumbia dari 229 papan dan 139 petak bambu. Pasar Tanah Abang terus mengalami perbaikan hingga akhir abad ke-19 dan bagian lantainya mulai dikeraskan dengan pondasi adukan. Pada tahun 1913, Pasar Tanah Abang kembali diperbaiki. Pada tahun 1926 pemerintah Batavia membongkar Pasar Tanah Abang dan diganti bangunan permanen berupa tiga los panjang dari tembok dan papan serta beratap genteng, dengan kantor pasarnya berada di atas bangunan pasar mirip kandang burung. Pelataran parkir di depan pasar menjadi tempat parkir kuda-kuda penarik delman dan gerobak. Di situ tersedia kobakan air yang cukup besar, dan di seberang jalan ada toko yang khusus menjual dedak makanan kuda. Beberapa puluh meter dari toko dedak ada sebuah gang yang dikenal sebagai Gang Madat, tempat lokalisasi para pemadat.\\nPada zaman pendudukan Jepang, pasar ini hampir tidak berfungsi, dan menjadi tempat para gelandangan.\\nPasar Tanah Abang semakin berkembang setelah dibangunnya Stasiun Tanah Abang. Di tempat tersebut mulai dibangun tempat-tempat seperti Masjid Al Makmur dan Klenteng Hok Tek Tjen Sien yang keduanya seusia dengan Pasar Tanah Abang. Pada tahun 1973, Pasar Tanah Abang diremajakan, diganti dengan 4 bangunan berlantai empat, dan sudah mengalami dua kali kebakaran, pertama tanggal 30 Desember 1978, Blok A di lantai tiga dan kedua menimpa Blok B tanggal 13 Agustus 1979. Pada tahun 1975 tercatat kiosnya ada 4.351 buah dengan 3.016 pedagang.\r\n', 'Jakarta\r\n', 4, -6.18901, 106.812, 'https://lampungpro.co/laravel-filemanager/photos/17/BARU3/IMG_20200406_080722.jpg'),
(63, 'Pecinan Glodok\r\n', 'Kawasan Glodok yang dikenal sebagai pusat perdagangan elektronik sendiri dulunya merupakan bekas Penjara Glodok yang angker. Lokasi ini berada di seberang Glodok-Pancoran di kawasan Harco. Sebelum terjadi kerusuhan Mei 1998, kawasan ini sempat menjadi pusat belanja elektronik bagi wisatawan Jepang, Taiwan, dan Hongkong. Di Pinangsia, masyarakat bisa menemukan produk perlengkapan rumah tangga dan bangunan. Selain wisata belanja, pecinan Glodok-Pancoran juga merupakan situs sejarah awal mula Kota Jakarta. Setelah etnis Tionghoa bermukim dan membangun bisnis, serta perkebunan tebu, Batavia mulai berkembang pesat.\r\n', 'Jakarta\r\n', 4.3, -6.14276, 106.814, 'https://asset-a.grid.id/crop/0x0:0x0/945x630/photo/2022/12/27/img-20221227-wa0004jpg-20221227104850.jpg'),
(64, 'Sea World\r\n', 'Seaworld Indonesia adalah sebuah miniatur pesona laut yang terdapat dalam kompleks wisata pertama di Taman Impian Jaya Ancol.\\nPada tanggal 2 Oktober 1992, Gubernur DKI masa itu, Wiyogo Atmodarminto meletakkan batu pertama pembangunan Seaworld Indonesia. Tidak sampai dua tahun, pada tanggal 3 Juni 1994 SeaWorld Indonesia sudah mulai beroperasi. Area Seaworld seluas 3 hektare dengan luas bangunan utama 4.500 m2 berisi berbagai macam akuarium, lorong Antasena (lorong bawah air), perpustakaan, museum, terapi ikan dokter, glow theatre, komputer edukatif layar sentuh berisi informasi berbagai spesies di Seaworld dan bermacam fasilitas pelengkap untuk pengunjung seperti tempat makan, toko suvenir, dan ruang serba guna.\r\n', 'Jakarta\r\n', 4.5, -6.12648, 106.843, 'https://kcic.co.id/lkapp/wp-content/uploads/2021/07/TLMD-SeaWorld.jpg'),
(65, 'Wisata Agro Edukatif Istana Susu Cibugary\r\n', 'Kawasan Wisata Agro Edukatif Istana Susu ?Cibugary? (Cibubur Garden Dairy ) merupakan suatu kawasan agro peternakan sapi perah bernuansa kebun dan taman. Wisata edukasi Cibubur Garden Dairy (CIBUGARY) merupakan salah wisata edukasi yang diminati siswa sekolah. Edukasi pengenalan peternakan sapi memberikan pengalaman yang tidak dapat dilupakan oleh siswa sekolah. Kegiatan edukasi yang tidak terlalu banyak dan menyenangkan ini sangat disukai siswa sekolah. Dengan berwisata edukasi ini dapat menambah ilmu dan pengalaman secara langsung bagi siswa sekolah. Paket wisata edukatif Istana Susu Cibugary dihargai antara Rp 35.000 sampai Rp 65.000 per orang. Harga tersebut sudah termasuk beberapa botol susu pasteurisasi dan goodybag cibugary.\r\n', 'Jakarta\r\n', 4.5, -6.35681, 106.906, 'https://fastly.4sqi.net/img/general/600x600/13586648_xDbLhAvCnVXE9EDwjNKiNeMuAiCIsqCmSzWnNTtHAPo.jpg'),
(66, 'Setu Babakan\r\n', 'Setu Babakan atau Danau Babakan terletak di Srengseng Sawah, kecamatan Jagakarsa, Kotamadya Jakarta Selatan, Indonesia dekat Depok yang berfungsi sebagai pusat Perkampungan Budaya Betawi, suatu area yang diperuntukkan untuk pelestarian warisan budaya Jakarta, yaitu budaya asli Betawi.\\nPerkampungan budaya Betawi ini didirikan pada tanggal 18 Agustus 2000 melalui Surat Keputusan Gubernur DKI Nomor 92 tahun 2000 yang dikeluarkan oleh Gubernur DKI Jakarta pada waktu itu yaitu Sutiyoso\\nSitu Babakan merupakan danau buatan dengan area 30 hektare (79 akre) dengan kedalaman 1-5 meter dimana airnya berasal dari Sungai Ciliwung dan saat ini digunakan sebagai tempat wisata alternatif, bagi warga dan para pengunjung.\\nTaman disekitarnya ditanami dengan beragam pohon buah-buahan yaitu Mangga, Palem, Melinjo, Rambutan, Jambu, Pandan, Kecapi, Jamblang, Krendang, Guni, Nangka Cimpedak, Nam-nam, dan Jengkol.\\nBanyak kuliner khas Betawi terdapat disini, antara lain Kerak Telor, Toge Goreng, Arum Manis, Rujak Bebek, Soto Betawi, Es Potong, Es Duren, Bir Pletok, Nasi Uduk, Nasi Ulam, Lontong Sayur, dll.\\nWisata budaya yang disajikan antara laim rumah-rumah khas Betawi yang dibagi menjadi 3 macam, pertama rumah Betawi gudang atau kandang, kedua rumah Betawi Kebaya atau Bapang, dan yang ketiga adalah rumah Joglo, hampir serupa dengan rumah khas Yogyakarta.\\nKeseniannya berupa Lenong, Tari Topeng, Tanjidor, Marawis, Gambang Kromong, Tari Lenggang Nyai, dan Tari Narojeng.\\nUpacara Adat yang ada di perkampungan Betawi Setu Babakan adalah Penganten Sunat, Pindah Rumah, Khatam Qur\'an, dan Nujuh Bulan.\\nMayoritas penduduk di Setu Babakan adalah Betawi, dengan program dari pemda DKI untuk memperbaiki sarana dan prasarana yang ada untuk mengakomodasi kebutuhan ruang terbuka hijau, serta area untuk resapan air, setu babakan berbenah diri dengan dukungan penuh dari pemda DKI\\nFungsi dari Setu ini bukan hanya untuk tempat melestarikan kebudayaan betawi yang makin tergerus oleh zaman, tetapi digunakan juga sebagai tempat alternatif rekreasi yang berlokasi di selatan jakarta. selain fungsi utamanya sebagai penampung air resapan untuk selatan jakarta,\r\n', 'Jakarta\r\n', 4.4, -6.34167, 106.824, 'https://upload.wikimedia.org/wikipedia/commons/2/25/Setu_Babakan.jpg'),
(67, 'Taman Suropati\r\n', 'Taman Suropati (awalnya bernama Burgemeester Bisschopplein) adalah nama sebuah taman di Jakarta. Pada awalnya nama taman ini diambil dari nama wali kota Batavia pertama, G.J. Bisshop (1916?1920). Taman ini merupakan pusat kawasan Menteng, berada tepat di antara pertemuan tiga jalan utama, yaitu Menteng Boulevard (Jalan Teuku Umar), Orange Boulevard (Jalan Diponegoro), dan Nassau Boulevard (Jalan Imam Bonjol). Lapangan yang kini disebut sebagai Taman Suropati ini sejak tahun 1920 sudah menggantikan lapangan bundar yang luas dalam Rencana Moojen. Taman Suropati, yang disebut-sebut sebagai titik nol Republik Indonesia, menjadi taman yang rindang dan sejak beberapa tahun yang lalu dihiasi dengan patung-patung karya pematung dari negara-negara pendiri Perhimpunan Bangsa-Bangsa Asia Tenggara.\r\n', 'Jakarta\r\n', 4.6, -6.1994, 106.833, 'https://ksmtour.com/media/images/articles11/taman-suropati-jakarta.jpg'),
(68, 'Grand Indonesia Mall\r\n\r\n', 'Grand Indonesia merupakan mal di Jakarta. Mal ini dibuka pada tahun 2007 oleh Presiden Susilo Bambang Yudhoyono. Pada tahun 2007, Hotel Indonesia mengalami pemugaran. Selanjutnya setelah dibuka kembali, hotel dikelola oleh grup Kempinski dan namanya diganti menjadi Hotel Indonesia-Kempinski.\r\n', 'Jakarta\r\n', 4.7, -6.19518, 106.82, 'https://www.pinhome.id/info-area/wp-content/uploads/2021/12/GI-Fokus-Jabar_11zon.jpg'),
(69, 'Skyrink - Mall Taman Anggrek\r\n', 'Salah satu arena ice skating Jakarta adalah Ice Skating Taman Anggrek. Area ice skating di Taman Anggrek dibuat seluas 1.248 meter persegi. Ice Skating Termegah Di Indonesia adalaH di Taman Anggrek. Tidak hanya termegah di Indonesia tetapi juga termegah di Asia.Maka dari itu tidak mengherankan bahwa banyak sekali pengunjung yang datang.\r\n', 'Jakarta\r\n', 4.5, -6.179, 106.792, 'https://upload.wikimedia.org/wikipedia/commons/1/1b/Ice_skating_Mall_Taman_Anggrek.jpg'),
(70, 'Istana Negara Republik Indonesia\r\n', 'Istana Negara merupakan pusat kegiatan pemerintahan negara. Banyak kegiatan-kegiatan bersifat kenegaraan digelar di Istana Negara. Seperti kegiatan pelantikan pejabat-pejabat penting negara, maupun pembukaan kongres bersifat nasional dan internasional. Istana Negara fungsinya lebih difokuskan kepada kegiatan resmi kepresidenan, yaitu sebagai kantor Presiden Republik Indonesia. Dilansir situs Sekretaris Negara (Sesneg), Istana Negara awalnya sebagai kediaman pribadi warga Belanda, J.A Van Braam.\r\n', 'Jakarta\r\n', 4.6, -6.16807, 106.824, 'https://asset.kompas.com/crops/x-mb5u8S2tkauhqnlWBiq0Ympyc=/0x0:1024x683/750x500/data/photo/2021/03/29/6061d157c67d0.jpg'),
(71, 'Museum Bahari Jakarta\r\n', 'Museum Bahari adalah museum yang menyimpan koleksi yang berhubungan dengan kebaharian dan kenelayanan bangsa Indonesia dari Sabang hingga Merauke yang berlokasi di seberang Pelabuhan Sunda Kelapa. Museum adalah salah satu dari delapan museum yang berada di bawah pengawasan dari Dinas Kebudayaan Permuseuman Provinsi Daerah Khusus Ibu kota Jakarta.\r\n', 'Jakarta\r\n', 4.4, -6.12696, 106.809, 'https://cdn2.tstatic.net/wartakotatravel/foto/bank/images/museum-bahari-3.jpg'),
(72, 'Jalan Braga\r\n', 'Jalan Braga adalah nama sebuah jalan utama di kota Bandung, Indonesia. Nama jalan ini cukup dikenal sejak masa pemerintahan Hindia Belanda. Sampai saat ini nama jalan tersebut tetap dipertahankan sebagai salah satu maskot dan objek wisata kota Bandung yang dahulu dikenal sebagai Parijs van Java.\r\n', 'Bandung\r\n', 4.7, -6.91505, 107.609, 'https://www.indonesia.travel/content/dam/indtravelrevamp/id-id/ide-liburan/menikmati-berbagai-sudut-paling-instagenic-di-jalan-braga/warenhuis.jpg');
